import React,{useState} from "react";


export default function Todo() {
  const [value, setValue] = useState('')
  const [todoList, setTodoList] = useState([])
  const getTodoValue = (event) => {
    setValue(event.target.value)
  }
  const addTodo = () => {
    // debugger
    let arr = [...todoList]
    arr.push(value)
    setTodoList(arr)
    localStorage.setItem("todos" , JSON.stringify(arr))
  }
const onLoadTodo = () => {
  let localStorageArr = localStorage.getItem("todos", JSON)
}
  return (
    <>
      <div className="header">
        <h1 className="main-heading">Todo app</h1>
      </div>
      <div className="main">
      <div>
        <input type="text" className="todo" size="60px" onChange={getTodoValue}/>
        <div className="btn">
          <button onClick={()=>addTodo()}>Add Todo</button>
          <button onClick="deleteAll()">Delete all</button>
        </div>
        </div> 
        <ul className="todo-list">
          {todoList.map(item => (
            <li>


            <img src="assets/icons8-checkmark-480-modified.png" className= "checkmark" alt=""/>
            <span>
            {item} 
            </span>


            <div className= "btn-div">
            <img src="assets/icons8-edit-150.png" onclick = "editTodo(this, ${arr.length})" className="eidt-icon" alt=""/>
            <img src="assets/icons8-bin-64.png" onclick = "deleteTodo(this, ${arr.length} )" className="bin-icon" alt=""/>
            </div>
            </li>
          ))

          }
        </ul>
    </div>
    </>
  );
}
